import React, { useState, useEffect } from 'react';
import { 
  DollarSign, 
  Users, 
  ShoppingCart, 
  TrendingUp,
  CreditCard,
  CheckCircle,
  Clock,
  AlertCircle,
  ArrowUpRight,
  ArrowDownRight,
  Activity,
  BarChart3,
  PieChart,
  Calendar,
  Download,
  Settings
} from 'lucide-react';
import './HomePage.css';

const HomePage = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalOrders: 0,
    activeUsers: 0,
    conversionRate: 0
  });

  const [recentOrders, setRecentOrders] = useState([]);
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    // Simulate data loading
    const loadData = () => {
      setStats({
        totalRevenue: 125430,
        totalOrders: 1847,
        activeUsers: 3241,
        conversionRate: 3.2
      });

      setRecentOrders([
        { id: 'ORD-001', customer: 'John Doe', amount: 1250, status: 'completed', time: '2 min ago', method: 'Credit Card' },
        { id: 'ORD-002', customer: 'Jane Smith', amount: 850, status: 'pending', time: '5 min ago', method: 'PayPal' },
        { id: 'ORD-003', customer: 'Mike Johnson', amount: 2100, status: 'processing', time: '8 min ago', method: 'Bank Transfer' },
        { id: 'ORD-004', customer: 'Sarah Wilson', amount: 450, status: 'completed', time: '12 min ago', method: 'Credit Card' },
        { id: 'ORD-005', customer: 'David Brown', amount: 3200, status: 'failed', time: '15 min ago', method: 'Credit Card' }
      ]);

      setChartData([
        { name: 'Jan', revenue: 4000, orders: 240 },
        { name: 'Feb', revenue: 3000, orders: 139 },
        { name: 'Mar', revenue: 2000, orders: 980 },
        { name: 'Apr', revenue: 2780, orders: 390 },
        { name: 'May', revenue: 1890, orders: 480 },
        { name: 'Jun', revenue: 2390, orders: 380 }
      ]);
    };

    loadData();
  }, []);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={16} className="status-icon completed" />;
      case 'pending':
        return <Clock size={16} className="status-icon pending" />;
      case 'processing':
        return <Activity size={16} className="status-icon processing" />;
      case 'failed':
        return <AlertCircle size={16} className="status-icon failed" />;
      default:
        return <Clock size={16} className="status-icon" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return '#10b981';
      case 'pending':
        return '#f59e0b';
      case 'processing':
        return '#3b82f6';
      case 'failed':
        return '#ef4444';
      default:
        return '#6b7280';
    }
  };

  const quickStats = [
    {
      title: 'Total Revenue',
      value: formatCurrency(stats.totalRevenue),
      change: '+12.5%',
      changeType: 'positive',
      icon: DollarSign,
      color: '#10b981',
      trend: [1, 3, 2, 4, 3, 5, 4]
    },
    {
      title: 'Total Orders',
      value: stats.totalOrders.toLocaleString(),
      change: '+8.2%',
      changeType: 'positive',
      icon: ShoppingCart,
      color: '#3b82f6',
      trend: [2, 4, 3, 5, 4, 6, 5]
    },
    {
      title: 'Active Users',
      value: stats.activeUsers.toLocaleString(),
      change: '+15.3%',
      changeType: 'positive',
      icon: Users,
      color: '#8b5cf6',
      trend: [1, 2, 3, 2, 4, 3, 5]
    },
    {
      title: 'Conversion Rate',
      value: `${stats.conversionRate}%`,
      change: '-2.1%',
      changeType: 'negative',
      icon: TrendingUp,
      color: '#f59e0b',
      trend: [5, 4, 3, 4, 3, 2, 3]
    }
  ];

  const paymentMethods = [
    { name: 'Credit Card', percentage: 65, amount: 81530, color: '#6366f1' },
    { name: 'Debit Card', percentage: 25, amount: 31358, color: '#8b5cf6' },
    { name: 'Bank Transfer', percentage: 10, amount: 12543, color: '#10b981' }
  ];

  return (
    <div className="home-page">
      <div className="page-header">
        <div className="header-content">
          <h1 className="page-title">Dashboard Overview</h1>
          <p className="page-description">Welcome back! Here's what's happening with your business today.</p>
        </div>
        <div className="header-actions">
          <button className="btn-secondary">
            <Calendar size={16} />
            Last 30 days
          </button>
          <button className="btn-primary">
            <Download size={16} />
            Export Report
          </button>
        </div>
      </div>

      {/* Quick Stats */}
      <div className="stats-grid">
        {quickStats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="stat-card">
              <div className="stat-header">
                <div className="stat-icon" style={{ backgroundColor: `${stat.color}15`, color: stat.color }}>
                  <Icon size={24} />
                </div>
                <div className={`stat-change ${stat.changeType}`}>
                  {stat.changeType === 'positive' ? <ArrowUpRight size={16} /> : <ArrowDownRight size={16} />}
                  {stat.change}
                </div>
              </div>
              <div className="stat-content">
                <div className="stat-value">{stat.value}</div>
                <div className="stat-label">{stat.title}</div>
              </div>
              <div className="stat-trend">
                <svg width="100%" height="40" viewBox="0 0 100 40">
                  <polyline
                    points={stat.trend.map((value, i) => `${i * (100 / (stat.trend.length - 1))},${40 - (value * 6)}`).join(' ')}
                    fill="none"
                    stroke={stat.color}
                    strokeWidth="2"
                    opacity="0.7"
                  />
                </svg>
              </div>
            </div>
          );
        })}
      </div>

      <div className="dashboard-content">
        {/* Recent Orders */}
        <div className="dashboard-card">
          <div className="card-header">
            <h2 className="card-title">Recent Orders</h2>
            <button className="view-all-btn">View All</button>
          </div>
          <div className="orders-list">
            {recentOrders.map((order) => (
              <div key={order.id} className="order-item">
                <div className="order-info">
                  <div className="order-id">{order.id}</div>
                  <div className="customer-name">{order.customer}</div>
                  <div className="payment-method">{order.method}</div>
                </div>
                <div className="order-details">
                  <div className="order-amount">{formatCurrency(order.amount)}</div>
                  <div className="order-status">
                    {getStatusIcon(order.status)}
                    <span style={{ color: getStatusColor(order.status) }}>
                      {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                    </span>
                  </div>
                </div>
                <div className="order-time">{order.time}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Revenue Chart */}
        <div className="dashboard-card">
          <div className="card-header">
            <h2 className="card-title">Revenue Overview</h2>
            <div className="chart-controls">
              <button className="chart-btn active">6M</button>
              <button className="chart-btn">1Y</button>
              <button className="chart-btn">All</button>
            </div>
          </div>
          <div className="chart-container">
            <div className="chart-legend">
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#6366f1' }}></div>
                <span>Revenue</span>
              </div>
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#8b5cf6' }}></div>
                <span>Orders</span>
              </div>
            </div>
            <div className="chart-area">
              <svg width="100%" height="200" viewBox="0 0 400 200">
                {/* Revenue line */}
                <polyline
                  points={chartData.map((item, i) => `${(i * 400) / (chartData.length - 1)},${200 - (item.revenue / 50)}`).join(' ')}
                  fill="none"
                  stroke="#6366f1"
                  strokeWidth="3"
                />
                {/* Orders line */}
                <polyline
                  points={chartData.map((item, i) => `${(i * 400) / (chartData.length - 1)},${200 - (item.orders / 5)}`).join(' ')}
                  fill="none"
                  stroke="#8b5cf6"
                  strokeWidth="3"
                  strokeDasharray="5,5"
                />
              </svg>
            </div>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        {/* Payment Methods */}
        <div className="dashboard-card">
          <div className="card-header">
            <h2 className="card-title">Payment Methods</h2>
            <PieChart size={20} className="card-icon" />
          </div>
          <div className="payment-methods">
            {paymentMethods.map((method, index) => (
              <div key={index} className="payment-method-item">
                <div className="method-info">
                  <div className="method-icon" style={{ backgroundColor: `${method.color}15`, color: method.color }}>
                    <CreditCard size={16} />
                  </div>
                  <div className="method-details">
                    <span className="method-name">{method.name}</span>
                    <span className="method-amount">{formatCurrency(method.amount)}</span>
                  </div>
                </div>
                <div className="method-percentage">
                  <span>{method.percentage}%</span>
                  <div className="percentage-bar">
                    <div 
                      className="percentage-fill" 
                      style={{ 
                        width: `${method.percentage}%`, 
                        backgroundColor: method.color 
                      }}
                    ></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="dashboard-card">
          <div className="card-header">
            <h2 className="card-title">Quick Actions</h2>
          </div>
          <div className="quick-actions">
            <button className="action-btn">
              <CreditCard size={20} />
              <span>Process Payment</span>
            </button>
            <button className="action-btn">
              <Users size={20} />
              <span>View Customers</span>
            </button>
            <button className="action-btn">
              <BarChart3 size={20} />
              <span>Generate Report</span>
            </button>
            <button className="action-btn">
              <Settings size={20} />
              <span>Settings</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
