import React, { useState, useEffect } from 'react';
import { 
  DollarSign, 
  Users, 
  ShoppingCart, 
  TrendingUp,
  CreditCard,
  CheckCircle,
  Clock,
  AlertCircle,
  ArrowUpRight,
  ArrowDownRight,
  Activity,
  BarChart3,
  PieChart,
  Calendar,
  Download,
  Settings
} from 'lucide-react';
import './HomePage.css';

const HomePage = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalOrders: 0,
    activeUsers: 0,
    conversionRate: 0
  });

  const [recentOrders, setRecentOrders] = useState([]);
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    // Simulate data loading with realistic values
    const loadData = () => {
      setStats({
        totalRevenue: 125430,
        totalOrders: 1847,
        activeUsers: 3241,
        conversionRate: 3.2
      });

      setRecentOrders([
        { 
          id: 'ORD-001', 
          customer: 'John Doe', 
          amount: 1250, 
          status: 'completed', 
          time: '2 min ago', 
          method: 'Credit Card',
          avatar: 'JD'
        },
        { 
          id: 'ORD-002', 
          customer: 'Jane Smith', 
          amount: 850, 
          status: 'pending', 
          time: '5 min ago', 
          method: 'PayPal',
          avatar: 'JS'
        },
        { 
          id: 'ORD-003', 
          customer: 'Mike Johnson', 
          amount: 2100, 
          status: 'processing', 
          time: '8 min ago', 
          method: 'Bank Transfer',
          avatar: 'MJ'
        },
        { 
          id: 'ORD-004', 
          customer: 'Sarah Wilson', 
          amount: 450, 
          status: 'completed', 
          time: '12 min ago', 
          method: 'Credit Card',
          avatar: 'SW'
        },
        { 
          id: 'ORD-005', 
          customer: 'David Brown', 
          amount: 3200, 
          status: 'failed', 
          time: '15 min ago', 
          method: 'Credit Card',
          avatar: 'DB'
        }
      ]);

      setChartData([
        { name: 'Jan', revenue: 4000, orders: 240 },
        { name: 'Feb', revenue: 3000, orders: 139 },
        { name: 'Mar', revenue: 2000, orders: 980 },
        { name: 'Apr', revenue: 2780, orders: 390 },
        { name: 'May', revenue: 1890, orders: 480 },
        { name: 'Jun', revenue: 2390, orders: 380 }
      ]);
    };

    loadData();
  }, []);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={16} className="status-icon completed" />;
      case 'pending':
        return <Clock size={16} className="status-icon pending" />;
      case 'processing':
        return <Activity size={16} className="status-icon processing" />;
      case 'failed':
        return <AlertCircle size={16} className="status-icon failed" />;
      default:
        return <Clock size={16} className="status-icon" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return '#22c55e';
      case 'pending':
        return '#f59e0b';
      case 'processing':
        return '#3b82f6';
      case 'failed':
        return '#ef4444';
      default:
        return '#64748b';
    }
  };

  const getStatusBgColor = (status) => {
    switch (status) {
      case 'completed':
        return '#f0fdf4';
      case 'pending':
        return '#fffbeb';
      case 'processing':
        return '#eff6ff';
      case 'failed':
        return '#fef2f2';
      default:
        return '#f8fafc';
    }
  };

  const quickStats = [
    {
      title: 'Total Revenue',
      value: formatCurrency(stats.totalRevenue),
      change: '+12.5%',
      changeType: 'positive',
      icon: DollarSign,
      color: '#22c55e',
      bgColor: '#f0fdf4',
      trend: [1, 3, 2, 4, 3, 5, 4]
    },
    {
      title: 'Total Orders',
      value: stats.totalOrders.toLocaleString(),
      change: '+8.2%',
      changeType: 'positive',
      icon: ShoppingCart,
      color: '#3b82f6',
      bgColor: '#eff6ff',
      trend: [2, 4, 3, 5, 4, 6, 5]
    },
    {
      title: 'Active Users',
      value: stats.activeUsers.toLocaleString(),
      change: '+15.3%',
      changeType: 'positive',
      icon: Users,
      color: '#8b5cf6',
      bgColor: '#faf5ff',
      trend: [1, 2, 3, 2, 4, 3, 5]
    },
    {
      title: 'Conversion Rate',
      value: `${stats.conversionRate}%`,
      change: '-2.1%',
      changeType: 'negative',
      icon: TrendingUp,
      color: '#f59e0b',
      bgColor: '#fffbeb',
      trend: [5, 4, 3, 4, 3, 2, 3]
    }
  ];

  const paymentMethods = [
    { name: 'Credit Card', percentage: 65, amount: 81530, color: '#3b82f6', icon: CreditCard },
    { name: 'Debit Card', percentage: 25, amount: 31358, color: '#8b5cf6', icon: CreditCard },
    { name: 'Bank Transfer', percentage: 10, amount: 12543, color: '#22c55e', icon: CreditCard }
  ];

  return (
    <div className="home-page">
      {/* Page Header */}
      <div className="page-header">
        <div className="header-content">
          <h1 className="page-title">Dashboard Overview</h1>
          <p className="page-description">Welcome back! Here's what's happening with your business today.</p>
        </div>
        <div className="header-actions">
          <button className="btn-secondary">
            <Calendar size={16} />
            <span>Last 30 days</span>
          </button>
          <button className="btn-primary">
            <Download size={16} />
            <span>Export Report</span>
          </button>
        </div>
      </div>

      {/* Quick Stats Grid */}
      <div className="stats-grid">
        {quickStats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="stat-card">
              <div className="stat-header">
                <div 
                  className="stat-icon" 
                  style={{ 
                    backgroundColor: stat.bgColor, 
                    color: stat.color 
                  }}
                >
                  <Icon size={24} strokeWidth={2} />
                </div>
                <div className={`stat-change ${stat.changeType}`}>
                  {stat.changeType === 'positive' ? 
                    <ArrowUpRight size={16} strokeWidth={2} /> : 
                    <ArrowDownRight size={16} strokeWidth={2} />
                  }
                  <span>{stat.change}</span>
                </div>
              </div>
              <div className="stat-content">
                <div className="stat-value">{stat.value}</div>
                <div className="stat-label">{stat.title}</div>
              </div>
              <div className="stat-trend">
                <svg width="100%" height="40" viewBox="0 0 100 40" className="trend-chart">
                  <polyline
                    points={stat.trend.map((value, i) => `${i * (100 / (stat.trend.length - 1))},${40 - (value * 6)}`).join(' ')}
                    fill="none"
                    stroke={stat.color}
                    strokeWidth="2"
                    opacity="0.7"
                  />
                </svg>
              </div>
            </div>
          );
        })}
      </div>

      {/* Dashboard Content Grid */}
      <div className="dashboard-grid">
        {/* Recent Orders */}
        <div className="dashboard-card recent-orders">
          <div className="card-header">
            <h2 className="card-title">Recent Orders</h2>
            <button className="view-all-btn">
              <span>View All</span>
              <ArrowUpRight size={16} strokeWidth={2} />
            </button>
          </div>
          <div className="orders-list">
            {recentOrders.map((order) => (
              <div key={order.id} className="order-item">
                <div className="order-avatar">
                  <span>{order.avatar}</span>
                </div>
                <div className="order-info">
                  <div className="order-customer">{order.customer}</div>
                  <div className="order-id">{order.id}</div>
                  <div className="order-method">{order.method}</div>
                </div>
                <div className="order-details">
                  <div className="order-amount">{formatCurrency(order.amount)}</div>
                  <div 
                    className="order-status"
                    style={{ 
                      backgroundColor: getStatusBgColor(order.status),
                      color: getStatusColor(order.status)
                    }}
                  >
                    {getStatusIcon(order.status)}
                    <span>{order.status.charAt(0).toUpperCase() + order.status.slice(1)}</span>
                  </div>
                </div>
                <div className="order-time">{order.time}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Revenue Chart */}
        <div className="dashboard-card revenue-chart">
          <div className="card-header">
            <h2 className="card-title">Revenue Overview</h2>
            <div className="chart-controls">
              <button className="chart-btn active">6M</button>
              <button className="chart-btn">1Y</button>
              <button className="chart-btn">All</button>
            </div>
          </div>
          <div className="chart-container">
            <div className="chart-legend">
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#3b82f6' }}></div>
                <span>Revenue</span>
              </div>
              <div className="legend-item">
                <div className="legend-color" style={{ backgroundColor: '#8b5cf6' }}></div>
                <span>Orders</span>
              </div>
            </div>
            <div className="chart-area">
              <svg width="100%" height="200" viewBox="0 0 400 200" className="chart-svg">
                {/* Grid lines */}
                <defs>
                  <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
                    <path d="M 40 0 L 0 0 0 40" fill="none" stroke="var(--border-primary)" strokeWidth="1" opacity="0.1"/>
                  </pattern>
                </defs>
                <rect width="100%" height="100%" fill="url(#grid)" />
                
                {/* Revenue line */}
                <polyline
                  points={chartData.map((item, i) => `${(i * 400) / (chartData.length - 1)},${200 - (item.revenue / 50)}`).join(' ')}
                  fill="none"
                  stroke="#3b82f6"
                  strokeWidth="3"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
                {/* Orders line */}
                <polyline
                  points={chartData.map((item, i) => `${(i * 400) / (chartData.length - 1)},${200 - (item.orders / 5)}`).join(' ')}
                  fill="none"
                  stroke="#8b5cf6"
                  strokeWidth="3"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeDasharray="5,5"
                />
              </svg>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Grid */}
      <div className="dashboard-grid">
        {/* Payment Methods */}
        <div className="dashboard-card payment-methods">
          <div className="card-header">
            <h2 className="card-title">Payment Methods</h2>
            <PieChart size={20} className="card-icon" strokeWidth={2} />
          </div>
          <div className="payment-methods-list">
            {paymentMethods.map((method, index) => {
              const Icon = method.icon;
              return (
                <div key={index} className="payment-method-item">
                  <div className="method-info">
                    <div 
                      className="method-icon" 
                      style={{ 
                        backgroundColor: `${method.color}15`, 
                        color: method.color 
                      }}
                    >
                      <Icon size={20} strokeWidth={2} />
                    </div>
                    <div className="method-details">
                      <span className="method-name">{method.name}</span>
                      <span className="method-amount">{formatCurrency(method.amount)}</span>
                    </div>
                  </div>
                  <div className="method-percentage">
                    <span className="percentage-text">{method.percentage}%</span>
                    <div className="percentage-bar">
                      <div 
                        className="percentage-fill" 
                        style={{ 
                          width: `${method.percentage}%`, 
                          backgroundColor: method.color 
                        }}
                      ></div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="dashboard-card quick-actions">
          <div className="card-header">
            <h2 className="card-title">Quick Actions</h2>
          </div>
          <div className="actions-grid">
            <button className="action-btn">
              <CreditCard size={24} strokeWidth={2} />
              <span>Process Payment</span>
            </button>
            <button className="action-btn">
              <Users size={24} strokeWidth={2} />
              <span>View Customers</span>
            </button>
            <button className="action-btn">
              <BarChart3 size={24} strokeWidth={2} />
              <span>Generate Report</span>
            </button>
            <button className="action-btn">
              <Settings size={24} strokeWidth={2} />
              <span>Settings</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
