      avatar: 'RT'
    },
    {
      id: 'ORD-008',
      customer: 'Emily White',
      email: 'emily@example.com',
      phone: '+1 (555) 890-1234',
      address: '258 Spruce Ave, San Diego, CA',
      amount: 950.00,
      status: 'completed',
      date: '2024-01-08',
      paymentMethod: 'PayPal',
      items: [
        { name: 'Professional Plan', price: 950.00, quantity: 1 }
      ],
      shipping: 'Standard',
      notes: 'Auto-renewal enabled',
      refundAmount: 0,
      transactionId: 'TXN-789456130',
      avatar: 'EW'
    }
  ];

  useEffect(() => {
    setIsLoading(true);
    // Simulate API call
    setTimeout(() => {
      setOrders(mockOrders);
      setFilteredOrders(mockOrders);
      setIsLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    let filtered = orders.filter(order => {
      const matchesSearch = order.customer.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          order.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          order.id.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStatus = statusFilter === 'all' || order.status === statusFilter;

      // Date range filter
      const orderDate = new Date(order.date);
      const now = new Date();
      let matchesDate = true;

      if (dateRange === 'today') {
        const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        matchesDate = orderDate >= today;
      } else if (dateRange === 'week') {
        const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
        matchesDate = orderDate >= weekAgo;
      } else if (dateRange === 'month') {
        const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
        matchesDate = orderDate >= monthAgo;
      }

      return matchesSearch && matchesStatus && matchesDate;
    });

    // Sort orders
    filtered.sort((a, b) => {
      let aValue, bValue;

      switch (sortBy) {
        case 'date':
          aValue = new Date(a.date);
          bValue = new Date(b.date);
          break;
        case 'amount':
          aValue = a.amount;
          bValue = b.amount;
          break;
        case 'customer':
          aValue = a.customer.toLowerCase();
          bValue = b.customer.toLowerCase();
          break;
        default:
          aValue = a[sortBy];
          bValue = b[sortBy];
      }

      if (sortOrder === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    setFilteredOrders(filtered);
  }, [orders, searchTerm, statusFilter, sortBy, sortOrder, dateRange]);

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={16} className="status-icon completed" />;
      case 'pending':
        return <Clock size={16} className="status-icon pending" />;
      case 'processing':
        return <RefreshCw size={16} className="status-icon processing" />;
      case 'failed':
        return <XCircle size={16} className="status-icon failed" />;
      case 'refunded':
        return <ArrowUpDown size={16} className="status-icon refunded" />;
      default:
        return <Clock size={16} className="status-icon" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return '#22c55e';
      case 'pending':
        return '#f59e0b';
      case 'processing':
        return '#3b82f6';
      case 'failed':
        return '#ef4444';
      case 'refunded':
        return '#8b5cf6';
      default:
        return '#64748b';
    }
  };

  const getStatusBgColor = (status) => {
    switch (status) {
      case 'completed':
        return '#f0fdf4';
      case 'pending':
        return '#fffbeb';
      case 'processing':
        return '#eff6ff';
      case 'failed':
        return '#fef2f2';
      case 'refunded':
        return '#faf5ff';
      default:
        return '#f8fafc';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatAmount = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const handleViewOrder = (order) => {
    setSelectedOrder(order);
    setShowOrderModal(true);
  };

  const handleRefresh = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  };

  const handleExport = () => {
    // Simulate export functionality
    const csvContent = [
      ['Order ID', 'Customer', 'Amount', 'Status', 'Payment Method', 'Date'].join(','),
      ...filteredOrders.map(order => [
        order.id,
        order.customer,
        order.amount,
        order.status,
        order.paymentMethod,
        order.date
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'orders.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const totalAmount = filteredOrders.reduce((sum, order) => sum + order.amount, 0);
  const completedOrders = filteredOrders.filter(order => order.status === 'completed').length;
  const pendingOrders = filteredOrders.filter(order => order.status === 'pending').length;
  const failedOrders = filteredOrders.filter(order => order.status === 'failed').length;

  const stats = [
    {
      title: 'Total Revenue',
      value: formatAmount(totalAmount),
      icon: DollarSign,
      color: '#22c55e',
      bgColor: '#f0fdf4'
    },
    {
      title: 'Completed Orders',
      value: completedOrders,
      icon: CheckCircle,
      color: '#22c55e',
      bgColor: '#f0fdf4'
    },
    {
      title: 'Pending Orders',
      value: pendingOrders,
      icon: Clock,
      color: '#f59e0b',
      bgColor: '#fffbeb'
    },
    {
      title: 'Failed Orders',
      value: failedOrders,
      icon: XCircle,
      color: '#ef4444',
      bgColor: '#fef2f2'
    }
  ];

  return (
    <div className="orders-page">
      {/* Page Header */}
      <div className="page-header">
        <div className="header-content">
          <h1 className="page-title">Order Management</h1>
          <p className="page-description">Manage and track all your orders</p>
        </div>
        <div className="header-actions">
          <button className="btn-secondary" onClick={handleRefresh} disabled={isLoading}>
            <RefreshCw size={16} className={isLoading ? 'animate-spin' : ''} strokeWidth={2} />
            <span>Refresh</span>
          </button>
          <button className="btn-primary" onClick={handleExport}>
            <Download size={16} strokeWidth={2} />
            <span>Export</span>
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-cards">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <div key={index} className="stat-card">
              <div 
                className="stat-icon" 
                style={{ 
                  backgroundColor: stat.bgColor, 
                  color: stat.color 
                }}
              >
                <Icon size={24} strokeWidth={2} />
              </div>
              <div className="stat-content">
                <span className="stat-value">{stat.value}</span>
                <span className="stat-label">{stat.title}</span>
              </div>
            </div>
          );
        })}
      </div>

      {/* Filters and Search */}
      <div className="filters-section">
        <div className="search-container">
          <div className="search-box">
            <Search size={20} strokeWidth={2} />
            <input
              type="text"
              placeholder="Search orders by customer, email, or ID..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filters-container">
            <button 
              className="filter-toggle"
              onClick={() => setShowFilters(!showFilters)}
            >
              <Filter size={16} strokeWidth={2} />
              <span>Filters</span>
              {showFilters ? <ChevronUp size={16} strokeWidth={2} /> : <ChevronDown size={16} strokeWidth={2} />}
            </button>

            {showFilters && (
              <div className="filter-panel">
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">All Status</option>
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                  <option value="processing">Processing</option>
                  <option value="failed">Failed</option>
                  <option value="refunded">Refunded</option>
                </select>

                <select
                  value={dateRange}
                  onChange={(e) => setDateRange(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">All Time</option>
                  <option value="today">Today</option>
                  <option value="week">Last 7 Days</option>
                  <option value="month">Last 30 Days</option>
                </select>

                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="filter-select"
                >
                  <option value="date">Sort by Date</option>
                  <option value="amount">Sort by Amount</option>
                  <option value="customer">Sort by Customer</option>
                  <option value="status">Sort by Status</option>
                </select>

                <button
                  onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                  className="sort-button"
                >
                  <ArrowUpDown size={16} strokeWidth={2} />
                  <span>{sortOrder === 'asc' ? 'Ascending' : 'Descending'}</span>
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Orders Table */}
      <div className="orders-table-container">
        <div className="table-header">
          <span className="results-count">
            Showing {filteredOrders.length} of {orders.length} orders
          </span>
        </div>

        <div className="table-wrapper">
          <table className="orders-table">
            <thead>
              <tr>
                <th>Customer</th>
                <th>Order ID</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Payment Method</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {isLoading ? (
                <tr>
                  <td colSpan="7" className="loading-row">
                    <div className="loading-spinner">
                      <RefreshCw size={24} className="animate-spin" strokeWidth={2} />
                      <span>Loading orders...</span>
                    </div>
                  </td>
                </tr>
              ) : filteredOrders.length === 0 ? (
                <tr>
                  <td colSpan="7" className="empty-row">
                    <div className="empty-state">
                      <div className="empty-icon">
                        <CreditCard size={48} strokeWidth={2} />
                      </div>
                      <h3>No orders found</h3>
                      <p>Try adjusting your search or filter criteria</p>
                    </div>
                  </td>
                </tr>
              ) : (
                filteredOrders.map((order) => (
                  <tr key={order.id} className="order-row">
                    <td>
                      <div className="customer-cell">
                        <div className="customer-avatar">
                          <span>{order.avatar}</span>
                        </div>
                        <div className="customer-info">
                          <span className="customer-name">{order.customer}</span>
                          <span className="customer-email">{order.email}</span>
                        </div>
                      </div>
                    </td>
                    <td>
                      <span className="order-id">{order.id}</span>
                    </td>
                    <td>
                      <span className="amount">{formatAmount(order.amount)}</span>
                    </td>
                    <td>
                      <div 
                        className="status-cell"
                        style={{ 
                          backgroundColor: getStatusBgColor(order.status),
                          color: getStatusColor(order.status)
                        }}
                      >
                        {getStatusIcon(order.status)}
                        <span className="status-text">
                          {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                        </span>
                      </div>
                    </td>
                    <td>
                      <span className="payment-method">{order.paymentMethod}</span>
                    </td>
                    <td>
                      <span className="date">{formatDate(order.date)}</span>
                    </td>
                    <td>
                      <div className="actions">
                        <button 
                          className="action-btn" 
                          title="View Details"
                          onClick={() => handleViewOrder(order)}
                        >
                          <Eye size={16} strokeWidth={2} />
                        </button>
                        <button className="action-btn" title="More Options">
                          <MoreVertical size={16} strokeWidth={2} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Order Detail Modal */}
      {showOrderModal && selectedOrder && (
        <div className="modal-overlay" onClick={() => setShowOrderModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Order Details - {selectedOrder.id}</h2>
              <button 
                className="close-modal"
                onClick={() => setShowOrderModal(false)}
                aria-label="Close modal"
              >

              </button>
            </div>
            <div className="modal-body">
              <div className="order-detail-section">
                <h3>Customer Information</h3>
                <div className="detail-grid">
                  <div className="detail-item">
                    <User size={16} strokeWidth={2} />
                    <span>{selectedOrder.customer}</span>
                  </div>
                  <div className="detail-item">
                    <Mail size={16} strokeWidth={2} />
                    <span>{selectedOrder.email}</span>
                  </div>
                  <div className="detail-item">
                    <Phone size={16} strokeWidth={2} />
                    <span>{selectedOrder.phone}</span>
                  </div>
                  <div className="detail-item">
                    <MapPin size={16} strokeWidth={2} />
                    <span>{selectedOrder.address}</span>
                  </div>
                </div>
              </div>

              <div className="order-detail-section">
                <h3>Order Information</h3>
                <div className="detail-grid">
                  <div className="detail-item">
                    <span className="detail-label">Amount:</span>
                    <span className="detail-value">{formatAmount(selectedOrder.amount)}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Status:</span>
                    <span className="detail-value" style={{ color: getStatusColor(selectedOrder.status) }}>
                      {selectedOrder.status.charAt(0).toUpperCase() + selectedOrder.status.slice(1)}
                    </span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Payment Method:</span>
                    <span className="detail-value">{selectedOrder.paymentMethod}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Date:</span>
                    <span className="detail-value">{formatDate(selectedOrder.date)}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Transaction ID:</span>
                    <span className="detail-value">{selectedOrder.transactionId}</span>
                  </div>
                  {selectedOrder.refundAmount > 0 && (
                    <div className="detail-item">
                      <span className="detail-label">Refund Amount:</span>
                      <span className="detail-value" style={{ color: '#ef4444' }}>
                        {formatAmount(selectedOrder.refundAmount)}
                      </span>
                    </div>
                  )}
                </div>
              </div>

              <div className="order-detail-section">
                <h3>Items</h3>
                <div className="items-list">
                  {selectedOrder.items.map((item, index) => (
                    <div key={index} className="item-row">
                      <span className="item-name">{item.name}</span>
                      <span className="item-quantity">Qty: {item.quantity}</span>
                      <span className="item-price">{formatAmount(item.price)}</span>
                    </div>
                  ))}
                </div>
              </div>

              {selectedOrder.notes && (
                <div className="order-detail-section">
                  <h3>Notes</h3>
                  <p className="order-notes">{selectedOrder.notes}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrdersPage;
